from django.contrib.auth import authenticate, login
from .models import News
def index(request):
    return render(request, 'index.html')

def index(request):
    news_articles = News.objects.all()  
    return render(request, 'index.html', {'news_articles': news_articles}) 
def news_detail(request, news_id):
    news_article = News.objects.get(id=news_id)
    return render(request, 'news_detail.html', {'news_article': news_article})
def login_view(request):
    if request.method == 'POST':
        username = request.POST['username']
        password = request.POST['password']
        user = authenticate(request, username=username, password=password)  # ตรวจสอบข้อมูลการล็อกอิน
        if user is not None:
            login(request, user)  # ล็อกอินผู้ใช้
            return redirect('/success/')  # หากสำเร็จ ไปหน้า success
        else:
            messages.error(request, 'Invalid username or password')  # ถ้าไม่สำเร็จ แสดง error
            return render(request, 'login.html')
    return render(request, 'login.html')

def add_user_view(request):
    if request.method == 'POST':
        username = request.POST['username']
        password = request.POST['password']
        
        # เพิ่มการสร้างผู้ใช้ใหม่
        if User.objects.filter(username=username).exists():
            messages.error(request, 'Username already exists')
        else:
            user = User.objects.create_user(username=username, password=password)
            user.save()
            messages.success(request, 'User created successfully')
            return redirect('login')

    return render(request, 'signup.html')
    
def signup_view(request):
    return render(request, 'signup.html')

def success_view(request):
    return render(request, 'success.html')

def clean_image_url(self):
    image_url = self.cleaned_data.get('image_url').strip()
    return image_url.replace('\r', '').replace('\n', '')

def search_suggestions(request):
    if 'q' in request.GET:
        query = request.GET.get('q')
        movies = Movie.objects.filter(title__icontains=query)[:5]  # ค้นหาจากชื่อหนัง
        suggestions = list(movies.values('title'))  # ดึงเฉพาะชื่อหนังออกมา
        return JsonResponse(suggestions, safe=False)
    return JsonResponse([], safe=False)

